import React from 'react';
import { PDFDocument } from 'pdf-lib';
import fs from 'fs';

const fillForm = async () => {
    // const formPath = '\fillable.pdf';
    // const formPdfBytes = await fetch(formPath).then(res => res.arrayBuffer())
    const pdfDoc = await PDFDocument.load(fs.readFileSync("fillable.pdf"));

    const form = pdfDoc.getForm();
   
    const pdfBytes = await pdfDoc.save();

    FileSaver.saveAs(pdfBytes, "pdf-lib_form_creation_example.pdf", "application/pdf");
}

const SavePDF  = () => {
    return (<button onclick={fillForm()}>Fill PDF</button>);
}

export default SavePDF;
